CREATE TABLE SOCIO (NIF_SOCIO BIGINT NOT NULL, APELLIDO1 VARCHAR(255) NOT NULL, APELLIDO2 VARCHAR(255), CP INTEGER, DIRECCION VARCHAR(255), EMAIL VARCHAR(255), ESTADO VARCHAR(255), FECHA_ALTA DATE, FECHA_NACIMIENTO DATE, MOVIL INTEGER, NOMBRE VARCHAR(255) NOT NULL, OBSERVACIONES VARCHAR(255), POBACION VARCHAR(255), PROVINCIA VARCHAR(255), TELEFONO INTEGER, PRIMARY KEY (NIF_SOCIO))
CREATE TABLE APADRINAMIENTOS (ID_APADRINAMIENTO BIGINT NOT NULL, FECHA_INICIO DATE, EMPLEADO_NIF_EMP BIGINT, JOVEN_ID BIGINT, SOCIO_NIF_SOCIO BIGINT, PRIMARY KEY (ID_APADRINAMIENTO))
CREATE TABLE JOVENES (ID BIGINT NOT NULL, APELLIDO1 VARCHAR(255), APELLIDO2 VARCHAR(255), COLONIA_ACTUAL VARCHAR(255), ESTADO VARCHAR(255), FECHA_ALTA DATE, FECHA_BAJA DATE, FECHA_NACIMIENTO DATE, FOTO_JOVEN VARCHAR(255), GRADO_CURSO INTEGER, NOMBRE VARCHAR(255), OBSERVACIONES VARCHAR(255), PROCEDENCIA VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE HISTORIAL_APADRINAMIENTOS (ID_H_APADRINAMIENTOS BIGINT NOT NULL, FECHA_ALTA DATE, FECHA_BAJA DATE, APADRINAMIENTO_ID_APADRINAMIENTO BIGINT, PRIMARY KEY (ID_H_APADRINAMIENTOS))
CREATE TABLE ROL (ID_ROL BIGINT NOT NULL, DESCRIPCION VARCHAR(255), NOMBRE VARCHAR(255) NOT NULL, PRIMARY KEY (ID_ROL))
CREATE TABLE EMPLEADO (NIF_EMP BIGINT NOT NULL, APELLIDO1 VARCHAR(255) NOT NULL, APELLIDO2 VARCHAR(255), DIRECCION VARCHAR(255), EMAIL VARCHAR(255), FECHA_ALTA DATE NOT NULL, FECHA_NACIMIENTO DATE, MOVIL VARCHAR(255) NOT NULL, NOMBRE VARCHAR(255) NOT NULL, POBLACION VARCHAR(255), PROVINCIA VARCHAR(255), TELEFONO VARCHAR(255) NOT NULL, ROL_ID_ROL BIGINT, PRIMARY KEY (NIF_EMP))
ALTER TABLE APADRINAMIENTOS ADD CONSTRAINT PDRNMENTOSSCNFSCIO FOREIGN KEY (SOCIO_NIF_SOCIO) REFERENCES SOCIO (NIF_SOCIO)
ALTER TABLE APADRINAMIENTOS ADD CONSTRAINT PDRNMNTOSMPLDNFEMP FOREIGN KEY (EMPLEADO_NIF_EMP) REFERENCES EMPLEADO (NIF_EMP)
ALTER TABLE APADRINAMIENTOS ADD CONSTRAINT PDRINAMIENTOSJVNID FOREIGN KEY (JOVEN_ID) REFERENCES JOVENES (ID)
ALTER TABLE HISTORIAL_APADRINAMIENTOS ADD CONSTRAINT HSTPDRNMNTDPDRNMNT FOREIGN KEY (APADRINAMIENTO_ID_APADRINAMIENTO) REFERENCES APADRINAMIENTOS (ID_APADRINAMIENTO)
ALTER TABLE EMPLEADO ADD CONSTRAINT EMPLEADOROL_ID_ROL FOREIGN KEY (ROL_ID_ROL) REFERENCES ROL (ID_ROL)
CREATE TABLE SEQUENCE (SEQ_NAME VARCHAR(50) NOT NULL, SEQ_COUNT DECIMAL(15), PRIMARY KEY (SEQ_NAME))
